mapper.select:
select dooo_id,dooo_name,remark,type,department_id,new_time,update_time,dooo_condition,approval_type,construction_dictionary_id from dooo
1=1
AND department_id =#{dooo.departmentId}
AND approval_type = #{approvalType}
AND dooo_name like CONCAT(CONCAT('%',#{dooo.doooName}), '%')
order by new_time desc

select doo.dooo_id,doo.dooo_name,doo.remark,doo.type,doo.department_id,doo.new_time,doo.update_time,doo.dooo_condition,dpt.department_name from dooo doo,department dpt
doo.department_id = dpt.department_id
and doo.dooo_name like CONCAT('%',#{doooName},'%')
and doo.department_id =#{departmentId}
and doo.type = #{type}
order by doo.new_time desc

SELECT d.dooo_name AS doooName, d.dooo_id AS doooId, de.department_name AS departmentName, de.department_id AS departmentId FROM dooo d INNER JOIN department de ON de.department_id = d.department_id
d.approval_type = 3
and d.dooo_name LIKE CONCAT('%',#{keyWord},'%')
and d.type like CONCAT('%',#{type},'%')
and d.department_id = #{departmentId}
LIMIT ${(page-1) * pageSize},${pageSize};
