mapper.select:
SELECT * FROM (select rownum r, b.version ,b.PROGRAM_ID,b.PROGRAM_NAME, b.PROGRAM_CODE, b.SERVICE_OBJECT, b.ADMIN_DEPT, b.REPORT_DATE, b.LATEST_UPDATE_DATE, ROW_NUMBER() over (partition by b.PROGRAM_CODE order by b.report_date desc) RN from PY_BASE.APPR_PROGRAM_DEF b where b.data_src = 'cityPull' and b.is_deleted = 0 -- and b.program_level = 0 and b.exchange = 1 -- and b.audit_status = 2
and b.PROGRAM_NAME like CONCAT(CONCAT('%',#{programName}),'%')
) where RN = 1 and r BETWEEN #{start} AND #{end}

select count(1) as totales from (select b.version, ROW_NUMBER() over (partition by b.PROGRAM_CODE order by b.report_date desc) RN from PY_BASE.APPR_PROGRAM_DEF b where b.data_src = 'cityPull' and b.is_deleted = 0 -- and b.program_level = 0 and b.exchange = 1 -- and b.audit_status = 2
and b.PROGRAM_NAME like CONCAT(CONCAT('%',#{programName}),'%')
) where RN = 1

select r.result_id,r.result_name, r.indate, r.program_id, r.ds_remark, r.stuff_type,r.law_force, r.is_relate_license, r.license_code
from PY_BASE.APPR_PROGRAM_LICENSE_RESULT r
where r.program_id = #{programId}

select y.content, y.order_num, y.seq, y.content_type, y.program_id
from PY_BASE.APPR_PROGRAM_ITEM_RIGHT_DUTY y
where y.program_id = #{programId}
